import React, { useState } from "react";
import { uploadBlueprint } from "../api";

// --- CORRECTED: Destructure all needed props ---
function FileUpload({ setReport, setIsLoading, setError, isLoading }) {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState("No file chosen");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setFileName(selectedFile.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError("Please select a file before analyzing.");
      return;
    }

    // --- CORRECTED: Reset state and add try/catch for loading and error handling ---
    setIsLoading(true);
    setError(null);
    setReport(null);

    try {
      const reportData = await uploadBlueprint(file);
      if (reportData.error) {
          setError(reportData.error);
      } else {
          setReport(reportData);
      }
    } catch (err) {
      const errorMessage = err.response?.data?.error || "An unknown error occurred.";
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="upload-section">
      <h3>Upload Blueprint for Analysis</h3>
      <form onSubmit={handleSubmit}>
        <label htmlFor="file-upload" className="custom-file-upload">
          Choose File
        </label>
        <input id="file-upload" type="file" onChange={handleFileChange} accept=".pdf,.dwg,.ifc" />
        <span id="file-name">{fileName}</span>
        <button type="submit" disabled={isLoading}>
          {isLoading ? "Analyzing..." : "Analyze"}
        </button>
      </form>
    </div>
  );
}

export default FileUpload;
